INSERT INTO public.vacancy (id, about, broker, hours, location, posting_date, salary, title, vacancy_number, vacancyurl) VALUES ('da0229d8-0563-4a5a-a3bc-4bd9983c9dd5', '<h1>UX designer</h1>
