INSERT INTO public.vacancy (id, about, broker, hours, location, posting_date, salary, title, vacancy_number, vacancyurl) VALUES ('62e08d01-9017-4479-946f-eb93766cbbe1', '<p>For a company focusing on high volume data movement/replication I am looking for a software engineer (C/C++).</p>
