INSERT INTO public.vacancy (id, about, broker, company, hours, posting_date, salary, title, vacancy_number, vacancyurl, location_id) VALUES ('c815eff3-6641-4e71-885e-ae659cd46fc6', '<p>For a company focusing on high volume data movement/replication I am looking for a software engineer (C/C++).</p>
