INSERT INTO public.vacancy (id, about, broker, company, hours, posting_date, salary, title, vacancy_number, vacancyurl, location_id) VALUES ('aa2a3835-37e4-4336-af74-e1248f3a42c8', '<strong>Meewerkend voorman Netwerkbeheer</strong>
