INSERT INTO public.vacancy (id, about, broker, company, hours, posting_date, salary, title, vacancy_number, vacancyurl, location_id) VALUES ('dcf1a4cc-6d2b-44a5-89dc-7f864f7440ed', '<p><b>How to succeed/ Technical Skills Required:</b></p>
<ul>
 <li>Process driven, <b>Front End 4+ years of hands on experience</b></li>
 <li><b>Java 8, Spring</b></li>
 <li><b>Open Shift</b></li>
