INSERT INTO public.vacancy VALUES ('7b6b7ce6-b247-4551-9e32-837ca79c704b', '<p>Voor een organisatie binnen toerisme/leisure en mobiliteit in Den Haag wordt er gezocht naar een ETL Developer.</p>
