INSERT INTO public.vacancy VALUES ('2cf6629c-58a7-40c1-919e-27203523dcad', 'Je hebt het HBO denkniveau en bij Plauti en hebt 2-5 jaar werkervaring en krijg een salaris tot € 4.000 en werkniveau van een Medior en mag sowieso Reiskostenvergoeding, Teamuitjes, Uitstekende secundaire arbeidsvoorwaarden, Pensioenregeling verwachten
<br>
