INSERT INTO public.vacancy VALUES ('b35d43cf-ca09-4d14-8258-7e88265b364e', 'Je bij Academic en mag sowieso Teamuitjes, Ontwikkelingsmogelijkheden, Onkostenvergoeding verwachten en bezit kennis over java, sql en hebt het HBO of MBO of WO denkniveau en werkniveau van een Lead of Medior of Senior
